mapper.select:
SELECT t1.id id, t1.name as name, t1.name_cn nameCn, t1.url url, t1.layer_type layerType, t1.feature_type featureType, t1.add_flag addFlag, t1.layer_table layerTable, t1.param_id paramId, t1.is_proxy isProxy, t1.proxy_url proxyUrl, t1.vector_layer_id vectorLayerId, t1.data as data, t1.extent extent, t1.dir_layer_id dirLayerId, t1.dir_path dirPath, t1.metadata_id metadataId, t1.base_color baseColor, t1.specular specular, t1.isSelfPoPup as isSelfPoPup, t1.selfPoPupContent as selfPoPupContent, t2.author_count authorCount FROM ( SELECT c.id AS dir_layer_id, c.xpath AS dir_path, c.order_nm, c.dir_order, t.* FROM ag_layer t, ( SELECT a.*, b.xpath, b.order_nm AS dir_order FROM ag_bim_layer a, ( SELECT * FROM ag_bim d WHERE d.xpath = #{xpath}
OR d.xpath LIKE CONCAT(#{xpath},'/%')
) b WHERE b.id = a.dir_id
AND a.id IN ( SELECT dir_layer_id FROM ag_user_bim WHERE is_base_map = #{isBaseMap} AND user_id IN ( SELECT id FROM ag_user WHERE id = #{userId} ) )
) c WHERE c.layer_id = t.id
AND t.name like CONCAT(CONCAT('%', TRIM(#{agLayer.name})),'%')
AND t.layer_type like '01%'
) t1, ( SELECT e.id, count(f.user_id) AS author_count FROM ag_bim_layer e LEFT JOIN ag_user_bim f ON e.id = f.dir_layer_id GROUP BY e.id ) t2 WHERE t1.dir_layer_id = t2.id ORDER BY t1.dir_order,t1.order_nm

