AND t.layer_type like '01%'
) t1, ( SELECT e.id, count(f.user_id) AS author_count FROM ag_bim_layer e LEFT JOIN ag_user_bim f ON e.id = f.dir_layer_id GROUP BY e.id ) t2 WHERE t1.dir_layer_id = t2.id ORDER BY t1.dir_order,t1.order_nm

SELECT * FROM (SELECT t3.*,d.`name` as dirName,d.xpath dirPath FROM (SELECT c.*,f.ID as userLayerId FROM ( SELECT t.id id, t.name as name, t.name_cn nameCn, t.url url, t.layer_type layerType, t.feature_type featureType, t.add_flag addFlag, t.layer_table layerTable, t.param_id paramId, t.is_proxy isProxy, t.proxy_url proxyUrl, t.vector_layer_id vectorLayerId, t.data as data, t.extent extent, t.metadata_id metadataId, t.visible_minzoom visibleMinZoom, t.visible_maxzoom visibleMaxZoom, t.base_color baseColor, t.specular specular, t.isSelfPoPup as isSelfPoPup, t.selfPoPupContent as selfPoPupContent, a.dir_id dirId, a.id dirLayerId FROM ag_layer t, ( SELECT * FROM ag_bim_layer WHERE id IN ( SELECT DISTINCT (dir_layer_id) FROM ag_user_bim WHERE user_id = #{userId} ) ) a WHERE a.layer_id = t.id ORDER BY a.order_nm ) c LEFT JOIN ag_user_bim f on dirLayerId = f.DIR_LAYER_ID AND f.USER_ID =#{userId}) t3 LEFT JOIN ag_bim d on dirId = d.ID
where t3.name like CONCAT(CONCAT('%', TRIM(#{name})),'%')
)t4

