mapper.select:
SELECT * FROM ( SELECT t1.tagId, t1.layerId, t2.name tagName FROM AG_TAGLAYER t1 LEFT JOIN AG_TAG t2 ON t1.tagId = t2.id ) w
INNER JOIN AG_OPENMAP_APPLYITEM m ON m.layerId = w.layerId AND m.completed = '0' AND m.loginName = #{loginName}
INNER JOIN AG_LAYER r ON w.layerId = r.id AND r.is_proxy = '1' AND r.is_external = '1'

SELECT 
FROM AG_OPENMAP_ATTACHFILE f
WHERE f.id = #{id}

mapper.insert:
INSERT INTO
AG_OPENMAP_AUDITLIST(id, serialNo, applicant, layerName, applyTime)
VALUES (
#{id}, #{serialNo}, #{applicant}, #{layerName}, #{applyTime}
)

mapper.select:
