mapper.select:
SELECT c.DIR_LAYER_ID, d.CURRENTPROCESSSTATUS, d.AUDIT_TIME, d.VALIDITYDATE, a.* FROM AG_LAYER a INNER JOIN AG_DIR_LAYER b on a.ID = b.LAYER_ID AND a.IS_PROXY = '1' AND a.IS_EXTERNAL = '1' INNER JOIN AG_OPENMAP_APPLY_ITEM c ON c.DIR_LAYER_ID = b.ID INNER JOIN AG_OPENMAP_APPLY d ON d.ID = c.APPLY_ID WHERE APPLICANT_LOGINNAME = #{applicantLoginName}
AND b.DIR_ID IN (SELECT ID FROM AG_DIR WHERE XPATH LIKE CONCAT(#{xpath}, '%'))
AND (a.NAME LIKE CONCAT(CONCAT('%', #{keyWord}), '%') OR a.NAME_CN like CONCAT(CONCAT('%', #{keyWord}), '%'))
AND d.CURRENTPROCESSSTATUS = #{applyStatus}
ORDER BY APPLYTIME DESC

SELECT b.ID AS DIR_LAYER_ID, a.* FROM AG_LAYER a INNER JOIN AG_DIR_LAYER b on a.ID = b.LAYER_ID AND a.IS_PROXY = '1' AND a.IS_EXTERNAL = '1' WHERE b.ID IN ( select DISTINCT DIR_LAYER_ID from AG_OPENMAP_APPLY c INNER JOIN AG_OPENMAP_APPLY_ITEM d on c.ID = d.APPLY_ID WHERE APPLICANT_LOGINNAME = #{applicantLoginName}
AND CURRENTPROCESSSTATUS = #{applyStatus}
)
AND b.DIR_ID IN (SELECT ID FROM AG_DIR WHERE XPATH LIKE CONCAT(#{xpath}, '%'))
AND (a.NAME LIKE CONCAT(CONCAT('%', #{keyWord}), '%') OR a.NAME_CN like CONCAT(CONCAT('%', #{keyWord}), '%'))

mapper.update:
UPDATE AG_OPENMAP_APPLY SET CURRENTPROCESSSTATUS = #{currentProcessStatus}, AUDITOR_LOGINNAME = #{auditorLoginName}, AUDITOR_USERNAME = #{auditorUserName},
AUDIT_OPINION = #{auditOpinion},
AUDIT_TIME = #{auditTime} WHERE id = #{id}

mapper.delete:
DELETE FROM AG_OPENMAP_CARTITEM
WHERE LOGINNAME = #{loginName}
AND DIR_LAYER_ID = #{dirLayerId}

mapper.select:
SELECT distinct a.* FROM AG_OPENMAP_APPLY a INNER JOIN AG_OPENMAP_APPLY_ITEM b ON a.ID = b.APPLY_ID INNER JOIN AG_DIR_LAYER c ON b.DIR_LAYER_ID = c.ID INNER JOIN AG_LAYER d ON c.LAYER_ID = d.ID WHERE 1=1
AND a.AUDITOR_LOGINNAME = #{auditorLoginName}
AND ( d.NAME LIKE CONCAT(CONCAT('%', #{keyWord}), '%') OR d.NAME_CN like CONCAT(CONCAT('%', #{keyWord}), '%') OR a.applicant LIKE CONCAT(CONCAT('%', #{keyWord}), '%') OR a.ID LIKE CONCAT(CONCAT('%', #{keyWord}), '%') OR a.WORKUNIT LIKE CONCAT(CONCAT('%', #{keyWord}), '%') OR a.AUDITOR_USERNAME LIKE CONCAT(CONCAT('%', #{keyWord}), '%') )
AND a.CURRENTPROCESSSTATUS = #{auditStatus}
ORDER BY APPLYTIME DESC

mapper.update:
UPDATE AG_OPENMAP_APPLY_ITEM
SET
    PROXY_URL = #{proxyUrl}
WHERE
    id = #{id}
