mapper.select:
SELECT *
FROM AG_TAG a
WHERE a.id = #{id}

mapper.delete:
BEGIN
DELETE
FROM AG_TAGLAYER
WHERE tagId in (
SELECT DISTINCT t.id FROM AG_TAG t WHERE t.catalogId = #{id}
);
DELETE
FROM AG_TAGCATALOG
WHERE id = #{id};
DELETE
FROM AG_TAG
WHERE catalogId = #{id};
END;

BEGIN
DELETE
FROM AG_TAG
WHERE id = #{id};
DELETE
FROM AG_TAGLAYER
WHERE tagId = #{id};
END;

mapper.update:
UPDATE AG_TAG
SET name = #{name}
WHERE id = #{id}

UPDATE AG_TAGCATALOG
SET name = #{name}
WHERE id = #{id}

mapper.delete:
DELETE FROM AG_TAGLAYER
WHERE layerId in

    #{lid}

mapper.select:
SELECT t.LAYERID, t.CURRENTPROCESSSTATUS applyStatus, count(*) count
FROM AG_OPENMAP_APPLYITEM t
WHERE t.COMPLETED = '0'



        AND t.layerId in (
        SELECT DISTINCT a.layerId FROM AG_TAGLAYER a
        WHERE a.tagId
        in (
        SELECT DISTINCT b.id FROM AG_TAG b WHERE b.catalogId = #{id}
        )
        )


        AND t.layerId in (
        SELECT DISTINCT a.layerId FROM AG_TAGLAYER a WHERE a.tagId = #{id}
        )



GROUP BY t.LAYERID, t.CURRENTPROCESSSTATUS ORDER BY t.CURRENTPROCESSSTATUS
