mapper.select:
select

,

from OPU_FUNC_WIDGET_CONFIG
where 1=1

   and FUNC_CODE = #{funcCode}


    and CONFIG_KEY = #{configKey}

select

,

from OPU_FUNC_WIDGET_CONFIG
where is_active='1'

  and FUNC_CODE = #{funcCode}


  and CONFIG_KEY = #{configKey}

select

,

from OPU_FUNC_WIDGET_CONFIG
where 1 = 1

  and FUNC_CODE = #{funcCode}


  and CONFIG_KEY = #{configKey}


  and FUNC_INVOKE_URL = #{funcInvokeUrl}

select
,
from OPU_FUNC_WIDGET_CONFIG where 1 = 1
and FUNC_INVOKE_URL = #{funcInvokeUrl}
and FUNC_CODE = #{funcCode}
order by config_key
